import React, {useState} from "react"
import './register.css';
import validator from 'validator'


export const  Register = (props) => {
    const[email, setEmail]  = useState('')
    // const[phone, setPhone]  = useState('')
    const [emailError, setEmailError] = useState('')
    const [isDisabled, setIsDisabled] = React.useState(true);
    const validatedEmail = email => typeof email === "string" && email.includes("@");
   

    const onEmailChange = event => {
        setEmail(event.target.value);
      };
    
      React.useEffect(() => {
        setIsDisabled(!validateEmail(email));
      }, [email]);
    

    const handleSubmit = (e) => {
        e.preventDefault();
            console.log(email);
    }

    
  const validateEmail = (e) => {
    var email = e.target.value
  
    if (validator.isEmail(email)) {
      setEmailError('Valid Email :)')
    } else {
      setEmailError('Enter valid Email!')
    }
  }
    
    return (
       
         <div>
         <h2 className ="head-text">Enter Email/Phone to recieve token</h2>
             <form className="login-form"  onSubmit={handleSubmit}>
           <label htmlFor="email">Email</label>
		   <input value={email} onChange={(e) => { setEmail(e.target.value); validateEmail(e);}}  type="email" placeholder="your email" id="email" name="email" required></input>
           <span style={{
          fontWeight: 'bold',
          color: 'white',
       padding :'0.2rem',
        }}>{emailError}</span>
		  
		   <button  className="send-btn"  type="button" value="button" disabled={isDisabled}>Send</button>
		 </form>
		<button className="link-btn" onClick={() =>  props.onFormSwitch("Phone")}>Use phone instead</button>
         </div>
      
        
    )
}
export default Register;
