import React, {useState} from "react"
import './number.css'

export const  Phone = (props) => {
    const[phone, setPhone]  = useState('')
    const[email, setEmail]  = useState('')
    const [isError, setIsError] = useState(false);

   

    const handleSubmit = (e) => {
        e.preventDefault();
            console.log(phone);
    }
    
    return (
       
         <div>
         <h2 className ="head-text">Enter Phone/Email to recieve token</h2>
             <form className="login-form"  onSubmit={handleSubmit}>
           <label htmlFor="email">Phone</label>
		   <input value={phone} onChange= {(e) => { setPhone(e.target.value);if (e.target.value.length > 10) {
            setIsError(true);
          }}} type="tel"  error={isError} placeholder=" +233 " id="phone" name="phone"></input>
          
		  
		   <button  className="send-btn"  type="submit"  onClick ={() =>  props.onFormSwitch("loginForm")} > Send </button>
		 </form>
		<button className="link-btn" onClick={() =>  props.onFormSwitch("Register")}>Use Email Instead</button>
         </div>
      
        
    )
}
export default Phone;
